list_directory_response = list_directory("/system/.ssh")

if isinstance(list_directory_response, list):
    ssh_keys = [item for item in list_directory_response if isinstance(item, str) and item.endswith(".pub")]

    if len(ssh_keys) > 0:
        public_ssh_key = read_file(f"/system/.ssh/{ssh_keys[0]}")
        add_ssh_key_response = git_add_ssh_key(public_ssh_key)
    else:
        raise ValueError("No public SSH key found in the specified directory.")
else:
    raise ValueError("Failed to list directory contents.")
