# Let's try searching for Adidas T-shirts without the rating filter to see if we can find any suitable options.
product_search = search_product(product_name="Adidas T-shirt")

if product_search:
    # Filter the products to find those with a rating higher than 4.5
    suitable_products = {product_id: details for product_id, details in product_search.items() if details.get('rating') > 4.5}

    if suitable_products:
        product_id = list(suitable_products.keys())[0]  # Buy the first suitable product found.
        cart_add_product(product_id=product_id, quantity=1)
        checkout_selected_cart(product_ids=[product_id], discount_code=None)
    else:
        raise ValueError("No suitable product with a rating higher than 4.5 found.")
else:
    raise ValueError("No products found.")
