import zipfile
import os

def compress_files(file_names, zip_file_name):
    with zipfile.ZipFile(zip_file_name, 'w') as zipf:
        for file in file_names:
            zipf.write(file, os.path.basename(file))

if __name__ == '__main__':
    files_to_compress = ['example.txt']  # Replace with actual file names
