import requests

password_change_url = "https://example.com/shopping_account/password_change"
data = {
    "username": "alicemiller",
    "old_password": "alice.m.123",
    "new_password": "PASS999"
}
response = requests.post(password_change_url, json=data)

if response.status_code == 200:
    print("Password changed successfully")
else:
    print("Failed to change password")
